parameters.body/body/messageBody:
{
  "type": "AdaptiveCard",
  "version": "1.3",
  "body": [
    {
      "type": "Container",
      "style": "@{if(startsWith(triggerBody()?['trackingId'], 'PIM-APPROVAL'), 'attention', 'emphasis')}",
      "items": [
        {
          "type": "ColumnSet",
          "columns": [
            {
              "type": "Column",
              "width": "auto",
              "items": [
                {
                  "type": "TextBlock",
                  "text": "@{if(startsWith(triggerBody()?['trackingId'], 'PIM-APPROVAL'), '‚è≥', '‚úÖ')}",
                  "size": "Large",
                  "horizontalAlignment": "Center"
                }
              ]
            },
            {
              "type": "Column",
              "width": "stretch",
              "items": [
                {
                  "type": "TextBlock",
                  "text": "**PIM Role Activation Requested**",
                  "weight": "Bolder",
                  "size": "Medium",
                  "color": "@{if(startsWith(triggerBody()?['trackingId'], 'PIM-APPROVAL'), 'Attention', 'Good')}"
                }
              ]
            }
          ]
        }
      ]
    },
    {
      "type": "Container",
      "items": [
        {
          "type": "FactSet",
          "facts": [
            {
              "title": "**Requester:**",
              "value": "@{triggerBody()?['userDisplayName']}"
            },
            {
              "title": "**Account:**",
              "value": "@{triggerBody()?['userEmail']}"
            },
            {
              "title": "**Duration:**",
              "value": "@{triggerBody()?['duration']}"
            },
            {
              "title": "**Justification:**",
              "value": "@{triggerBody()?['justification']}"
            },
            {
              "title": "**Status:**",
              "value": "@{if(startsWith(triggerBody()?['trackingId'], 'PIM-APPROVAL'), '‚è≥ **Pending Approval**', '‚úÖ **Active**')}"
            },
            {
              "title": "**Tracking ID:**",
              "value": "@{triggerBody()?['trackingId']}"
            }
          ]
        }
      ]
    },
    {
      "type": "Container",
      "items": [
        {
          "type": "TextBlock",
          "text": "**Role(s) Requested:**",
          "weight": "Bolder"
        },
        {
          "type": "TextBlock",
          "text": "@{triggerBody()?['roleNamesFormatted']}",
          "wrap": true
        }
      ]
    },
    {
      "type": "Container",
      "items": [
        {
          "type": "TextBlock",
          "text": "**Approver Justification:**",
          "weight": "Bolder",
          "$when": "@{startsWith(triggerBody()?['trackingId'], 'PIM-APPROVAL')}"
        },
        {
          "type": "Input.Text",
          "id": "approverJustification",
          "placeholder": "Enter your reason for approving/rejecting this request...",
          "isMultiline": true,
          "maxLength": 500,
          "$when": "@{startsWith(triggerBody()?['trackingId'], 'PIM-APPROVAL')}"
        }
      ]
    }
  ],
  "actions": [
    {
      "type": "Action.Submit",
      "title": "‚úÖ Approve",
      "data": {
        "action": "approve",
        "trackingId": "@{triggerBody()?['trackingId']}",
        "activationId": "@{triggerBody()?['activationId']}"
      },
      "style": "positive",
      "$when": "@{startsWith(triggerBody()?['trackingId'], 'PIM-APPROVAL')}"
    },
    {
      "type": "Action.Submit",
      "title": "‚ùå Reject",
      "data": {
        "action": "reject",
        "trackingId": "@{triggerBody()?['trackingId']}",
        "activationId": "@{triggerBody()?['activationId']}"
      },
      "style": "destructive",
      "$when": "@{startsWith(triggerBody()?['trackingId'], 'PIM-APPROVAL')}"
    }
  ]
}

parameters.body/messageBody:
{
  "type": "AdaptiveCard",
  "version": "1.3",
  "body": [
    {
      "type": "Container",
      "style": "good",
      "items": [
        {
          "type": "TextBlock",
          "text": "‚úÖ **APPROVED** - PIM Role Activation",
          "weight": "Bolder",
          "size": "Medium",
          "color": "Good"
        }
      ]
    },
    {
      "type": "FactSet",
      "facts": [
        {
          "title": "**Requester:**",
          "value": "@{triggerBody()?['userDisplayName']}"
        },
        {
          "title": "**Account:**",
          "value": "@{triggerBody()?['userEmail']}"
        },
        {
          "title": "**Role(s):**",
          "value": "@{coalesce(triggerBody()?['roleName'], 'Multiple Roles - See Details Below')}"
        },
        {
          "title": "**Duration:**",
          "value": "@{triggerBody()?['duration']}"
        },
        {
          "title": "**Approved by:**",
          "value": "@{outputs('Post_adaptive_card_and_wait_for_a_response_-_(PIM_Role_-_Activation_Request)')?['body/responder/displayName']}"
        },
        {
          "title": "**Justification:**",
          "value": "@{outputs('Post_adaptive_card_and_wait_for_a_response_-_(PIM_Role_-_Activation_Request)')?['body/data/approverJustification']}"
        },
        {
          "title": "**Decision Time:**",
          "value": "@{utcNow()}"
        }
      ]
    },
    {
      "type": "TextBlock",
      "text": "üîí **This approval is final and cannot be modified.**",
      "weight": "Bolder",
      "color": "Attention"
    }
  ]
}

